import React from 'react';
import { StyleSheet, Text, TextInput, View, Button } from 'react-native';
import { getAllWords } from '../services/restclient';

export default class ArvattavaSana extends React.Component {
    constructor() {
        super()
        this.state = {
            word: "",
            id: "",
            randomWord: "",
            allWords: []
        }
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    componentDidMount = () => {
        this.updateView();
    }

    updateView() {
        getAllWords().then(allWords => {
            this.setState({ allWords });
        }).catch(err => {
            console.error("Caught an error", err);
            this.setState({ error: err.message })
        });
    }

//     getAllWords()
//     .then(response => response.json())
//     .then(response => {
//         const { words } = response.data
//         this.setState({ allWords: words })
//     })
// }

    handleSubmit(event) {
        event.preventDefault()
        const randNum = Math.floor(Math.random() * this.state.allWords.length)
        const randWord = this.state.allWords[randNum]
        this.setState({ randomWord: randWord })
    }

    render() {
        return (
            <View onSubmit={this.handleSubmit}>
                <Text>Word generator {'\n'}</Text>
                <Text>Click on the Button</Text>
                <View>
                    <Button title="Press me" onPress={this.handleSubmit} />
                    <Text style={styles.word}>{this.state.randomWord}</Text>
                </View>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    word: {
        position: "absolute",
        textAlign: "center",
        fontSize: 25,
        textTransform: "uppercase",
        color: "black",
        letterSpacing: 1,
    }
})
